import React, {useState, useEffect} from 'react';
import styles from './Chart.module.css';
import cx from 'classnames';
import {fetchDailyData} from '../../api';
import {Bar, Line} from 'react-chartjs-2';
const Chart = () => {
	const [dailyData, setDailyData] = useState([]);
	useEffect(() => {
		const daily = async () => {
			const data = await fetchDailyData();
			setDailyData(data);
		};
		console.log(dailyData);
		daily();
	}, []);
	const lineChartData = () => {
		return {
			// labels: ['January', 'February', 'March', 'April', 'May', 'June', 'July'],
			labels: dailyData.map(({reportDate}) => reportDate),
			// labels: dailyData(({reportDate}) => reportDate),
			datasets: [
				{
					label: 'Infected',
					borderColor: 'green',
					backGroundColor: 'rgba(70, 70, 70, 0.8)',
					fill: true,
					fillColor: 'rgba(70, 70, 70, 0.8)',
					strokeColor: 'rgba(70, 70, 70, 0.8)',
					pointColor: 'rgba(220,220,220,1)',
					pointStrokeColor: '#fff',
					pointHighlightFill: '#fff',
					pointHighlightStroke: 'rgba(220,220,220,1)',
					data: dailyData.map(({confirmed: {total}}) => total),
				},
				{
					label: 'Deaths',
					borderColor: 'red',
					backGroundColor: 'rgba(255, 0, 0, 0.8)',
					fill: true,
					fillColor: 'rgba(255, 0, 0,0.2)',
					strokeColor: 'rgba(255, 0, 0,1)',
					pointColor: 'rgba(151,187,205,1)',
					pointStrokeColor: '#fff',
					pointHighlightFill: '#fff',
					pointHighlightStroke: 'rgba(151,187,205,1)',
					data: dailyData.map(({deaths: {total}}) => total),
				},
			],
		};
	};

	const lineChart = dailyData.length !== 0 ? <Line data={lineChartData()} /> : null;

	return <div className={styles.container}>{lineChart}</div>;
};

export default Chart;
