import axios from 'axios';

const url = 'https://covid19.mathdro.id/api';

const fetchData = async () => {
	try {
		const {
			data: {
				confirmed: {value},
				recovered,
				lastUpdate,
				deaths,
			},
		} = await axios.get(url);
		return {value, recovered, lastUpdate, deaths};
	} catch (error) {
		console.log(error.message);
	}
};

export default fetchData;
